import React, {Component} from 'react';
import '../App.css';
import '../index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';
import NotAllowed from './NotAllowed';
import Allowed from './Allowed';

class AddItem extends Component {


    state = {
        nomeUtente: this.props.location.state.nomeUtente,
        password: this.props.location.state.password
    };

    handleFile = (e) => {
        console.log(e.target.files[0]);
        this.setState({
            selectedFile: e.target.files[0]
        });
    };

    handleName = (e) => {
        this.setState({
            nomeOggetto:e.target.value
        })
    }

    handleEvent = (e) => {
        this.setState({
            evento:e.target.value
        })
    }

    handleDesc = (e) => {
        this.setState({
            descrizione:e.target.value
        })
    }

    fileUploadHandler = (e) => {
        const fd = new FormData();
        fd.append('name',this.state.nomeOggetto);
        fd.append('event',this.state.evento);
        fd.append('description',this.state.descrizione);
        fd.append('productImage',this.state.selectedFile,this.state.selectedFile.name);

        axios.post('http://localhost:5000/api/items',fd)
            .then(res => {
                console.log(res);
            })
    };

    render() {
        return (
            <div className="container mt-5">
                {(this.state.nomeUtente == "") && (
                    <NotAllowed/>
                )}

                {(this.state.nomeUtente == "") && (
                    <Allowed/>
                )}
            </div>
        );
    }
}

export default AddItem;
