import React,{useState, useEffect} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody
  } from 'reactstrap';
import '../App.css';

function Laurea() {

    const [loaded, setLoaded] = useState(false);
    const [items, setItems] = useState([]);

    var path="http://localhost:5000/";
    useEffect(() => {
        fetchItems();
      },[]);

      const fetchItems = async () => {
          await axios.get("http://localhost:5000/api/items/laurea")
            .then(res => {
                setItems(res.data);
                console.log(res.data[0].productImage);
                setLoaded(true);
            })
            .catch(err => {
                console.log(err);
            });
      }

    return (
        <div className="container">
            <h1 className="text-center mt-5">Laurea</h1>
            {(!loaded) && (
                <p className="text-center">Caricamento...</p>
            )}
            <div className="row">
                {items.map(item => (
                    <div className="col-lg-3 col-md-4 col-sm-6 mt-3">
                        <Card className="flip-card">
                            <div className="flip-card-inner">
                                <div className="flip-card-front">
                                    <CardImg width="100%" src={path+item.productImage} alt="Card image cap" />
                                </div>
                                <div className="flip-card-back">
                                    <CardBody>
                                        <h2>{item.name}</h2>
                                        <hr/>
                                        <p>{item.description}</p>
                                    </CardBody>
                                </div>
                            </div>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Laurea;
