import React,{useState, useEffect} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, Alert
  } from 'reactstrap';

function Laurea() {

    const [loaded, setLoaded] = useState(false);
    const [items, setItems] = useState([]);

    var path="http://localhost:5000/updates/";
    useEffect(() => {
        fetchItems();
      },[]);

      const fetchItems = async () => {
          await axios.get("http://localhost:5000/api/items/laurea")
            .then(res => {
                setItems(res.data);
                console.log(res.data.productImage);
                setLoaded(true);
            })
            .catch(err => {
                console.log(err);
            });
      }

    return (
        <div>
            <h1 className="text-center mt-5">Laurea</h1>
            {(!loaded) && (
                <p className="text-center">Fetching items...</p>
            )}
            <div className="row">
                {items.map(item => (
                    <div className="col-lg-3 col-md-4 col-sm-6">
                        <Card>
                            <CardImg top width="100%" src={path+item.productImage} alt="Card image cap" />
                            <CardBody>
                                <CardTitle>{item.name}</CardTitle>
                                <CardText>{item.description}</CardText>
                            </CardBody>
                        </Card>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Laurea;
