import React, {Component} from 'react';
import '../App.css';
import '../index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';

class AddItem extends Component {

    state = {
        nomeOggetto:"",
        evento:"",
        descrizione:"",
        selectedFile:null
    };

    handleFile = (e) => {
        console.log(e.target.files[0]);
        this.setState({
            selectedFile: e.target.files[0]
        });
    };

    handleName = (e) => {
        console.log(e.target.value);
    }

    fileUploadHandler = (e) => {
        const fd = new FormData();
        fd.append('')
        fd.append('image',this.state.selectedFile,this.state.selectedFile.name);
    };

    render() {
        return (
            <div className="container">
                <Form>
                    <FormGroup row>
                        <Label for="exampleEmail" sm={2}>Nome Oggetto</Label>
                        <Col sm={10}>
                            <Input type="email" name="nome" id="nome" placeholder="Nome dell'oggettino" onChange={this.handleName}/>
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="exampleEmail" sm={2}>Evento</Label>
                        <Col sm={10}>
                            <Input type="email" name="email" id="exampleEmail" placeholder="Descrizione dell'evento" />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="exampleText" sm={2}>Descrizione</Label>
                        <Col sm={10}>
                            <Input type="textarea" name="text" id="exampleText" placeholder="Descrizione dell'oggettino"/>
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="exampleFile" sm={2}>File</Label>
                        <Col sm={10}>
                            <Input type="file" name="file" id="exampleFile" onChange={this.handleFile}/>
                        </Col>
                    </FormGroup>
                    <FormGroup check row>
                        <Col sm={{ size: 10, offset: 2 }} className="text-center mb-sm-0 mb-3">
                            <Button color="danger" onClick={this.fileUploadHandler}>Aggiungi</Button>
                        </Col>
                    </FormGroup>
                </Form>
            </div>
        );
    }
}

export default AddItem;
