import React,{Component} from 'react';
import {Link} from 'react-router-dom';

class Auth extends Component {

    state = {
        nomeUtente:"",
        password:""
    }

    render() {
        return (
            <div>
                <div className="row justify-content-center">
                    <h1>Effettua il login prima di entrare nell'area privata</h1>
                </div>
                <div className="row justify-content-center">
                    <form>
                        <div className="row">
                            <div className="input-group mb-3">
                                <input type="text" className="form-control w-100" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"></input>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-group mb-3">
                                <input type="password" className="form-control w-100" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1"></input>
                            </div>
                        </div>
                        <div className="row">
                            <Link to="/areapersonale">
                                <button className="btn btn-danger" type="submit">Accedi</button>
                            </Link>
                        </div>
                    </form>
                </div>
            </div>
        );
    }

}

export default Auth;
