import React, {Component} from 'react';
import '../App.css';
import '../index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import axios from 'axios';

class AddItem extends Component {


    state = {
        allowed: (typeof(this.props.location.state.allowed) === undefined) ? false : this.props.location.state.allowed,
        nomeOggetto:"",
        evento:"",
        descrizione:"",
        selectedFile:""
    };

    handleFile = (e) => {
        console.log(e.target.files[0]);
        this.setState({
            selectedFile: e.target.files[0]
        });
    };

    handleName = (e) => {
        this.setState({
            nomeOggetto:e.target.value
        })
    }

    handleEvent = (e) => {
        this.setState({
            evento:e.target.value
        })
    }

    handleDesc = (e) => {
        this.setState({
            descrizione:e.target.value
        })
    }

    fileUploadHandler = (e) => {
        const fd = new FormData();
        fd.append('name',this.state.nomeOggetto);
        fd.append('event',this.state.evento);
        fd.append('description',this.state.descrizione);
        fd.append('productImage',this.state.selectedFile,this.state.selectedFile.name);

        axios.post('http://localhost:5000/api/items',fd)
            .then(res => {
                console.log(res);
            })
            .catch(err => {
                console.log(err)
            })
    };

    render() {
        return (
            <div className="container mt-5">
                {(!this.state.allowed) && (
                    <div className="row justify-content-center">
                        <h1>Non sei autorizzato ad entrare in questa sezione</h1>
                    </div>
                )}

                {(this.state.allowed) && (
                    <Form>
                        <FormGroup row>
                            <Label for="exampleEmail" sm={2}>Nome Oggetto</Label>
                            <Col sm={10}>
                                <Input type="text" name="nome" id="nome" placeholder="Nome dell'oggettino" onChange={this.handleName}/>
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="exampleEmail" sm={2}>Evento</Label>
                            <Col sm={10}>
                                <Input type="text" name="event" id="evento" placeholder="Descrizione dell'evento" onChange={this.handleEvent}/>
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="exampleText" sm={2}>Descrizione</Label>
                            <Col sm={10}>
                                <Input type="textarea" name="description" id="description" placeholder="Descrizione dell'oggettino"onChange={this.handleDesc}/>
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="exampleFile" sm={2}>File</Label>
                            <Col sm={10}>
                                <Input type="file" name="file" id="exampleFile" onChange={this.handleFile}/>
                            </Col>
                        </FormGroup>
                        <FormGroup check row>
                            <Col sm={{ size: 10, offset: 2 }} className="text-center mb-sm-0 mb-3">
                                <Button color="danger" onClick={this.fileUploadHandler}>Aggiungi</Button>
                            </Col>
                        </FormGroup>
                </Form>
                )}
            </div>
        );
    }
}

export default AddItem;
