    const [loaded, setLoaded] = useState(false);
    const [error, setError] = useState(false);
    const [item, setItem] = useState([]);

    var errAlert;

    useEffect(() => {
        fetchItems();
      },[]);

      const fetchItems = async () => {
          await axios.get("http://localhost:5000/api/items/laurea")
            .then(res => {
                console.log(res.data);
