import React, {Component} from 'react';
import '../App.css';
import '../index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';

class AddItem extends Component {

    handleFile = (e) => {
        console.log(e.target.files[0]);
    }

    fileUploadHandler = () => {

    }

    render() {
        return (
            <div className="container">
                <Form>
                    <FormGroup row>
                        <Label for="exampleEmail" sm={2}>Nome Oggetto</Label>
                        <Col sm={10}>
                            <Input type="email" name="email" id="exampleEmail" placeholder="Nome dell'oggettino" />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="exampleEmail" sm={2}>Evento</Label>
                        <Col sm={10}>
                            <Input type="email" name="email" id="exampleEmail" placeholder="Descrizione dell'evento" />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="exampleText" sm={2}>Descrizione</Label>
                        <Col sm={10}>
                            <Input type="textarea" name="text" id="exampleText" placeholder="Descrizione dell'oggettino"/>
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="exampleFile" sm={2}>File</Label>
                        <Col sm={10}>
                            <Input type="file" name="file" id="exampleFile" onChange={this.handleFile}/>
                        </Col>
                    </FormGroup>
                    <FormGroup check row>
                        <Col sm={{ size: 10, offset: 2 }} className="text-center mb-sm-0 mb-3">
                            <Button color="danger">Aggiungi</Button>
                        </Col>
                    </FormGroup>
                </Form>
            </div>
        );
    }
}

export default AddItem;
