import Axios from 'axios';
import React,{Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

class Auth extends Component {

    state = {
        nomeUtente:"",
        password:"",
        allowed: false
    }

    handleUser = (e) => {
        this.setState({
            nomeUtente:e.target.value
        });
    };

    handlePass = (e) => {
        this.setState({
            passowrd: e.target.value
        });
    };

    checkData = (e) => {
        const fd = new FormData();
        fd.append("nomeUtente",this.state.nomeUtente);
        fd.append("password",this.state.password);

        axios.post("http://localhost:5000/api/users", fd) 
            .then(res => {
                console.log(res);
            });
    }

    render() {
        return (
            <div>
                <div className="row justify-content-center">
                    <h1>Effettua il login prima di entrare nell'area privata</h1>
                </div>
                <div className="row justify-content-center">
                    <form>
                        <div className="row">
                            <div className="input-group mb-3">
                                <input type="text" className="form-control w-100" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" onChange={handleUser}></input>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-group mb-3">
                                <input type="password" className="form-control w-100" placeholder="Password" aria-label="Password" aria-describedby="basic-addon1" onChange={handlePass}></input>
                            </div>
                        </div>
                        <div className="row">
                            <Link to={{
                                pathname: '/areapersonale',
                                state: {
                                    nomeUtente: this.state.nomeUtente,
                                    password: this.state.password
                                }
                            }}>
                                <button className="btn btn-danger" type="submit" onClick = {checkData}>Accedi</button>
                            </Link>
                        </div>
                    </form>
                </div>
            </div>
        );
    }

}

export default Auth;
